<template lang="pug">
v-app
  v-snackbar(
    app,
    v-model='$notification.showing',
    :color='$notification.color',
    :timeout='5000'
  ) {{ $notification.text }}
  sync-dialog(:open.sync='syncDialog', :options.sync='options')
  v-app-bar.app-bar(
    app,
    extended,
    flat,
    color='primary',
    dark,
    extension-height='48',
    height='64'
  )
    v-toolbar-items
      svg.icon(preserveAspectRatio='xMidYMid meet', viewBox='0 0 24 24')
        use(:href='iconUrl + "#ao3e-logo-main"')
    v-toolbar-title(v-if='$vuetify.breakpoint.xsOnly') Reading List
    v-toolbar-title(v-else) AO3 Enhancements Reading List
    v-spacer
    v-tooltip(bottom)
      template(v-slot:activator='{ on, attrs }')
        v-badge.mr-4(color='green', content='', overlap, :value='false')
          v-btn(
            v-bind='attrs',
            v-on='on',
            icon,
            v-if='$vuetify.breakpoint.xsOnly',
            @click='syncDialog = true'
          )
            v-icon {{ icons.mdiReload }}
          v-btn(
            v-bind='attrs',
            v-on='on',
            plain,
            v-else,
            @click='syncDialog = true'
          )
            v-icon.mr-2.ml-0(right) {{ icons.mdiReload }}
            span Sync
      span Sync
    v-tooltip(bottom)
      template(v-slot:activator='{ on, attrs }')
        v-btn(
          href='options_ui.html',
          v-bind='attrs',
          v-on='on',
          icon,
          v-if='$vuetify.breakpoint.xsOnly'
        )
          v-icon {{ icons.mdiCog }}
        v-btn(href='options_ui.html', v-bind='attrs', v-on='on', plain, v-else)
          v-icon.mr-2.ml-0(right) {{ icons.mdiCog }}
          span Options
      span Open AO3 Enhancements Options
  v-main(v-if='ready')
    v-row(align='center', justify='center')
      v-col(cols='12', sm='10', md='10', lg='8', xl='6')
        .toolbar-wrapper(
          v-intersect='{ handler: onToolbarIntersect, options: { threshold: [1.0] } }'
        )
          v-toolbar.elevation-0
            v-row.flex-sm-nowrap
              v-col.flex-grow-1.pt-0.pt-sm-3(cols='12', sm='auto')
                v-text-field(
                  v-model='searchModel',
                  solo,
                  single-line,
                  hide-details,
                  placeholder='Search works',
                  :dense='$vuetify.breakpoint.xsOnly'
                )
                  template(v-slot:prepend-inner)
                    v-icon {{ icons.mdiMagnify }}
              v-col.d-flex.justify-center.py-0.py-sm-3(cols='12', sm='auto')
                v-btn-toggle(
                  v-model='filterStatusModel',
                  :dense='$vuetify.breakpoint.xsOnly',
                  mandatory
                )
                  v-tooltip(
                    v-for='item in filterStatus',
                    bottom,
                    :key='item.value'
                  )
                    template(v-slot:activator='{ on, attrs }')
                      v-btn(:value='item.value', v-bind='attrs', v-on='on')
                        v-icon(:class='`status--${item.value}`') {{ item.icon }}
                    span {{ item.title }}
        v-card(:style='cardStyles')
          div
            v-divider
            v-card-text.px-0.py-0
              v-data-iterator(
                :items='works',
                :items-per-page='-1',
                hide-default-footer,
                group-by='status',
                :custom-sort='sort',
                :custom-filter='filter',
                item-key='workId',
                :search='searchModel',
                ref='iterator'
              )
                template(v-slot:no-data)
                  v-row.pt-2.pb-4.px-3.px-sm-0(no-gutters)
                    v-spacer
                    v-col.text-center(cols='12', sm='6')
                      h1.text-subtitle-1 Works that you're reading and have read will show up here.
                      h1.text-subtitle-2 Goto AO3 to get started :)
                    v-spacer
                template(v-slot:no-results)
                  v-row.pt-2.pb-4.px-3.px-sm-0(no-gutters)
                    v-spacer
                    v-col.text-center(cols='12', sm='6')
                      h1.text-subtitle-1 No works found with current search and filter.
                    v-spacer
                template(v-slot:default='{ groupedItems: groups }')
                  template(v-for='{ name, items: groupedWorks } in groups')
                    v-expansion-panels.sharp(
                      accordion,
                      v-model='open',
                      ref='panels'
                    )
                      .status-header.d-flex.justify-center
                        div
                          v-tooltip(bottom)
                            template(v-slot:activator='{ on, attrs }')
                              v-icon(
                                :class='`status--${groupedWorks[0].status}`',
                                v-bind='attrs',
                                v-on='on'
                              ) {{ statusIcons[groupedWorks[0].status] }}
                            span {{ upperStatusText(groupedWorks[0].status) }}
                          span.py-2.subtitle-1.pl-2(
                            v-if='$vuetify.breakpoint.xsOnly'
                          ) {{ upperStatusText(groupedWorks[0].status) }}
                      reading-list-entry(
                        v-for='(work, index) in groupedWorks',
                        :work='workMapObject[work.workId]',
                        :key='work.workId',
                        :id='`work-${work.workId}`',
                        @remove='remove(work.workId)',
                        :style='index === 0 && $vuetify.breakpoint.smAndUp ? "margin-top: -64px" : ""'
                      )
</template>

<script lang="ts">
import { Component, Vue } from 'vue-property-decorator';
import {
  mdiPencil,
  mdiTrashCan,
  mdiClose,
  mdiCog,
  mdiMagnify,
  mdiAlertOctagonOutline,
  mdiMenuOpen,
  mdiOpenInNew,
  mdiBook,
  mdiReload,
} from '@mdi/js';
import Fuse from 'fuse.js';
import debounce from 'just-debounce-it';

import { options, Options } from '@/common/options';
import {
  ContentDataWrapper,
  upperStatusText,
  WorkStatus,
  WORK_STATUSES_ICONS,
} from '@/common/readingListData';
import { api } from '@/common/api';
import { childLogger } from '@/common/logger';

import ReadingListEntry from './components/ReadingListEntry.vue';
import SyncDialog from './components/SyncDialog.vue';
import ReadingListWork from './ReadingListWork';

type WorkMapObject<T> = { [workId: string]: T };

@Component({
  components: { ReadingListEntry, SyncDialog },
})
export default class ReadingList extends Vue {
  iconUrl = browser.runtime.getURL('icons/icon.svg');
  dataWrapper!: ContentDataWrapper<typeof ReadingListWork>;
  statusIcons = WORK_STATUSES_ICONS;
  upperStatusText = upperStatusText;
  filterStatus = Object.entries(this.statusIcons).map(([value, icon]) => ({
    icon,
    value,
    title: value === 'all' ? 'All' : upperStatusText(value as WorkStatus),
  }));
  filterStatusModel = 'all';
  icons = {
    mdiPencil,
    mdiTrashCan,
    mdiClose,
    mdiCog,
    mdiMagnify,
    mdiAlertOctagonOutline,
    mdiMenuOpen,
    mdiOpenInNew,
    mdiBook,
    mdiReload,
  };
  fuse!: Fuse<ReadingListWork>;
  searchModel = '';
  options = options.DEFAULT;
  open: null | number = 1;
  workMapObject: WorkMapObject<ReadingListWork> = {};
  syncDialog = false;
  debouncedSetOptions = debounce(this.setOptions.bind(this), 250);
  ready = false;
  workWatchers: WorkMapObject<() => void> = {};
  logger = childLogger('ReadingList');

  async setOptions(newOptions: Options): Promise<void> {
    if (!this.ready) return;
    await options.set(newOptions);
  }

  get works(): ReadingListWork[] {
    return Array.from(Object.values(this.workMapObject));
  }

  get cardStyles(): unknown {
    if (this.$vuetify.breakpoint.lgAndUp) {
      return {
        zIndex: '5',
      };
    }
    return {};
  }

  setupFuse(): void {
    this.fuse = new Fuse(this.works, {
      keys: [
        { name: 'title', weight: 0.7 },
        { name: 'author', weight: 0.3 },
      ],
    });
  }

  setupWorkWatcher(workId: string): void {
    this.workWatchers[workId] = this.$watch(
      () => this.workMapObject[workId],
      () => {
        console.log('save', workId);
        const work = this.workMapObject[workId];
        if (work) {
          work.save().catch((e) => console.error(e));
        }
      },
      { deep: true }
    );
  }

  async created(): Promise<void> {
    this.options = await options.get(options.ALL);
    this.dataWrapper = new ContentDataWrapper(ReadingListWork);
    const workMap = await this.dataWrapper.get();
    // Convert to object cause dumb vue2
    this.workMapObject = Object.fromEntries(
      Array.from(workMap).map(([workId, work]) => [workId.toString(), work])
    );
    this.logger.log('works:', this.workMapObject);

    Object.keys(this.workMapObject).forEach(this.setupWorkWatcher.bind(this));

    this.setupFuse();

    this.dataWrapper.addListener((workId, work) => {
      this.logger.log('change', workId, work);
      if (work === null) {
        this.workWatchers[workId]();
        delete this.workWatchers[workId];
        Vue.delete(this.workMapObject, workId);
      } else {
        this.workWatchers[workId] && this.workWatchers[workId]();
        Vue.set(this.workMapObject, workId, work);
        this.$nextTick(() => this.setupWorkWatcher(workId.toString()));
      }
    }, null);

    this.$nextTick(() => {
      this.ready = true;
      // If got here via work link
      const query = new URL(window.location.href).searchParams;
      const show = query.get('show');
      if (show) {
        const workId = parseInt(show);
        this.open = workId;
        void this.$vuetify.goTo(`#work-${workId}`);
      }

      this.$watch(
        'options',
        () => {
          this.debouncedSetOptions(this.options);
        },
        { deep: true }
      );
    });
  }

  sort(works: ReadingListWork[]): ReadingListWork[] {
    const statusOrder = ['reading', 'toRead', 'onHold', 'read', 'dropped'];
    return works.sort((a: ReadingListWork, b: ReadingListWork) => {
      const status =
        statusOrder.indexOf(a.status!) - statusOrder.indexOf(b.status!);
      if (status !== 0) return status;
      const unread =
        (a.firstUnreadChapterIndex !== undefined ? -1 : 0) -
        (b.firstUnreadChapterIndex !== undefined ? -1 : 0);
      if (unread !== 0) return unread;
      if (this.searchModel) return 0;
      return a.title.localeCompare(b.title);
    });
  }

  filter(): ReadingListWork[] {
    let works = this.searchModel
      ? this.fuse.search(this.searchModel).map((r) => r.item)
      : this.works;
    if (this.filterStatusModel !== 'all') {
      works = works.filter((work) => work.status === this.filterStatusModel);
    }
    return works;
  }

  onToolbarIntersect([{ intersectionRatio, target }]: [
    IntersectionObserverEntry
  ]): void {
    target.children[0].classList.toggle('elevation-5', intersectionRatio < 1);
    target.children[0].classList.toggle('elevation-0', intersectionRatio >= 1);
  }

  remove(workId: number): void {
    api.readingListSet.sendBG(workId, null).catch((e) => console.error(e));
    this.workWatchers[workId]();
    delete this.workWatchers[workId];
    Vue.delete(this.workMapObject, workId);
  }
}
</script>

