<style lang="scss" scoped>
@import '~vuetify/src/styles/settings/_variables';
.icon {
  margin-left: -6px !important;
  margin-right: 14px !important;
}
.icon {
  color: #ffffff;
  width: 48px;
  cursor: default;
}
.v-icon.huge {
  width: 100%;
  ::v-deep svg {
    width: 100%;
    height: 100%;
    max-height: 30vh;
  }
}
.sharp {
  border-radius: 0;
  ::v-deep > *::before {
    box-shadow: none;
  }
  &:last-of-type {
    border-bottom-left-radius: 4px;
    border-bottom-right-radius: 4px;
  }
}
.toolbar-wrapper {
  position: sticky;
  top: -1px;
  z-index: 10;

  margin-top: -112px;
  padding-top: 65px;
  height: 173px;
  @media #{map-get($display-breakpoints, 'sm-and-up')} {
    padding-top: 49px;
    height: 113px;
  }
  @media #{map-get($display-breakpoints, 'lg-and-up')} {
    margin-top: -64px;
    padding-top: 1px;
    height: 64px;
  }

  .v-toolbar {
    height: 100% !important;
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
    ::v-deep .v-toolbar__content {
      height: 100% !important;
    }
  }
}
.status-header {
  position: sticky;
  width: 100%;
  top: 64px;
  // padding-top: 1px;
  height: 32px;
}
@media #{map-get($display-breakpoints, 'sm-and-up')} {
  .status-header {
    height: 64px;
  }
  .status-header > div {
    position: absolute;
    top: 0;
    left: -32px;
  }
}
</style>

<style lang="scss">
@use "sass:map";
$status-colors: (
  'read': hsl(240, 50%, 50%),
  'toRead': hsl(0, 0%, 50%),
  'reading': hsl(120, 50%, 50%),
  'dropped': hsl(0, 50%, 50%),
  'onHold': hsl(60, 50%, 50%),
);

@each $name, $color in $status-colors {
  .status--#{$name} {
    &.v-expansion-panel {
      border-left: 5px solid $color;
    }
    &.v-icon,
    &.v-rating .v-icon {
      color: $color !important;
      caret-color: $color !important;
    }
  }
}
</style>